package java_fundamentals;
import java.util.*;
 class question4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		if(a<0) {
			System.out.print("Negative");
		}
		else if(a>0) {
			System.out.print("Positive");
		}
		else {
			System.out.print("Zero");
		}

	}
	}

